package practice;

public class a2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 7, b;
		
		b = a--;
		System.out.println(a);
		System.out.println(b);
		System.out.println();
		
		b = --a;
		System.out.println(a);
		System.out.println(b);
		System.out.println();
		
		b = a++;
		System.out.println(a);
		System.out.println(b);
		System.out.println();
		
		b = ++a;
		System.out.println(a);
		System.out.println(b);
		System.out.println();
	}

}
