package ch4;

public class For3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("구구단 3단 출력");
		int dan = 3;
		for(int i = 1; i <10; i++) {
			System.out.println(dan + "*" + i + "=" + dan*i);
		}
		
		System.out.println();
		int dan1 = 7;
		for(int i = 1; i <10; i++) {
			System.out.println(dan1 + "*" + i + "=" + dan1*i);
		}
		
	}

}
